INSERT INTO `app_merchant` VALUES ('2e7fe280d91ded48798365961db89951', '嘉地酒园', '贺兰东麓，三百良田，中国好酒，嘉地天成。', 'defalut/dfa1147248e147a3899210904fad0d94..jpg', '<p>嘉地酒园，英文名称 Jade Vineyard 寓意一块美玉，坐落在银川贺兰山东麓。作为中国新兴精品酒庄，怀揣着要做中国顶级葡萄酒的梦想，在贺兰山东麓海拔1180米、亿万年处女地辛勤拓荒开垦，酒庄占地22公顷，葡萄园种植面积15公顷。嘉地酒园始终以国际水准为标杆，尊重自然，力求展现中国风土特色。嘉地酒园，用国际视野，做中国好酒。</p>', '', '2021-01-19 19:27:11', '', '2021-01-21 16:13:10', 305, 0x646566616C75742F39613965396663643137346234336366623635336638643063636365336638632E2E706E67, 0x646566616C75742F64666131313437323438653134376133383939323130393034666164306439342E2E6A7067, NULL, 1);
