mapper.select:
SELECT p.`name` ,process_key AS processKey,d.audit,d.check_type AS checkType ,d.type,p.bean,d.`status`,d.create_time AS createTime,d.end_time AS endTime , d.business_id AS businessId,d.start_user AS startUser,p.cc,p.description,d.step FROM (SELECT process_id,h.create_time,business_id,h.audit,h.check_type,h.`status`,end_time,h.step,h.type,h.start_user FROM `his_process` h WHERE h.id IN (SELECT MAX(id) FROM his_process where ((audit = #{hisProcess.userId} AND check_type= 1) OR (check_type = 2 AND audit =#{hisProcess.deptId}) OR (check_type = 3 AND audit = #{hisProcess.roleId}) OR (check_type = 4 AND audit = #{hisProcess.companyUserId}) ) GROUP BY business_id) ) d INNER JOIN process p ON p.id = d.process_id
and d.start_user= #{hisProcess.startUser}
ORDER BY d.end_time DESC
